import React, { useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for navigation

function Form() {
  const navigate = useNavigate(); // Initialize navigate function

  const [formData, setFormData] = useState({
    eventName: '',
    eventDate: new Date(),
    question: ''
  });

  const [formSubmitted, setFormSubmitted] = useState(false);
  const [error, setError] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleDateChange = (date) => {
    setFormData({
      ...formData,
      eventDate: date
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setError(''); // Reset error state
    if (formData.eventName.trim() === '' || formData.question.trim() === '') {
      setError('Please fill in all fields');
      return;
    }
    setFormSubmitted(true);
    // Navigate to FormSubmittedPage
    navigate('/form-submitted'); // Replace '/form-submitted' with your actual route
  };

  return (
    <div className="flex justify-center items-center h-screen">
      <form onSubmit={handleSubmit} className="max-w-md bg-white rounded-lg shadow-md p-8">
        <h2 className="text-2xl mb-4 text-center font-bold text-blue-600">Event Form</h2>
        {error && <p className="text-red-500 mb-4">{error}</p>}
        <div className="mb-4">
          <label htmlFor="eventName" className="block text-sm font-bold mb-2">Event Name:</label>
          <input
            type="text"
            id="eventName"
            name="eventName"
            value={formData.eventName}
            onChange={handleChange}
            className="w-full border border-gray-300 rounded-md p-2 focus:outline-none focus:border-blue-500"
          />
        </div>
        <div className="mb-4">
          <label htmlFor="eventDate" className="block text-sm font-bold mb-2">Event Date:</label>
          <DatePicker
            id="eventDate"
            selected={formData.eventDate}
            onChange={handleDateChange}
            className="w-full border border-gray-300 rounded-md p-2 focus:outline-none focus:border-blue-500"
          />
        </div>
        <div className="mb-4">
          <label htmlFor="question" className="block text-sm font-bold mb-2">Question:</label>
          <input
            type="text"
            id="question"
            name="question"
            value={formData.question}
            onChange={handleChange}
            className="w-full border border-gray-300 rounded-md p-2 focus:outline-none focus:border-blue-500"
          />
        </div>
        <button type="submit" className="w-full bg-green-500 text-white py-2 px-4 rounded-md hover:bg-green-600">
          Submit
        </button>
      </form>
    </div>
  );
}

export default Form;
