import React, { useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

function Form() {
  const [formData, setFormData] = useState({
    eventName: '',
    eventDate: new Date(),
    question: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleDateChange = (date) => {
    setFormData({
      ...formData,
      eventDate: date
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData);
  };

  return (
    <form onSubmit={handleSubmit} className="max-w-md mx-auto bg-gray-100 p-8 rounded-lg shadow-lg">
      <h2 className="text-2xl mb-4 text-center font-bold text-blue-600">Event Form</h2>
      <div className="mb-4">
        <label htmlFor="eventName" className="block text-sm font-bold mb-2">Event Name:</label>
        <input
          type="text"
          id="eventName"
          name="eventName"
          value={formData.eventName}
          onChange={handleChange}
          className="w-full border border-gray-300 rounded-md p-2 focus:outline-none focus:border-blue-500"
        />
      </div>
      <div className="mb-4">
        <label htmlFor="eventDate" className="block text-sm font-bold mb-2">Event Date:</label>
        <DatePicker
          id="eventDate"
          selected={formData.eventDate}
          onChange={handleDateChange}
          className="w-full border border-gray-300 rounded-md p-2 focus:outline-none focus:border-blue-500"
        />
      </div>
      <div className="mb-4">
        <label htmlFor="question" className="block text-sm font-bold mb-2">Question:</label>
        <input
          type="text"
          id="question"
          name="question"
          value={formData.question}
          onChange={handleChange}
          className="w-full border border-gray-300 rounded-md p-2 focus:outline-none focus:border-blue-500"
        />
      </div>
      <button type="submit" className="w-full bg-blue-500 text-white py-2 px-4 rounded-md hover:bg-blue-600">
        Submit
      </button>
    </form>
  );
}

export default Form;
